import React, { useState } from "react";
import "./Card.css";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { addtocart } from "../actions/index";

function Card({ item, setDetailpage, setProductDetail, setEdit }) {
  const [openAdd, setopenAdd] = useState(false);
  const [number, setNumber] = useState(1);
  const dispatch = useDispatch();
  const cart = {
    name: item.name,
    number: number,
  };
  const add = () => {
    setNumber(number + 1);
    addtocart(dispatch)(cart);
  };
  return (
    <>
      <div className="box">
        <img
          src={item.source}
          alt="logo"
          className="image"
          onClick={() => {
            setDetailpage(true);
            setProductDetail({
              category: item.category,
              name: item.name,
              source: item.source,
              price: item.price,
              quantity: item.quantity,
              description: item.description,
            });
          }}
        />
        <div className="name">
          <span>{item.name}</span>
        </div>
        <div className="price">
          <span>${item.price}</span>
        </div>
        <div className="buttom-part">
          {openAdd ? (
            <>
              <div className="adding-extend">
                <button className="minus" onClick={() => setNumber(number - 1)}>
                  -
                </button>
                <div className="number">
                  <span>{number}</span>
                </div>

                <button className="add" onClick={() => add()}>
                  +
                </button>
              </div>
            </>
          ) : (
            <button className="edit-button1" onClick={() => setopenAdd(true)}>
              Add
            </button>
          )}

          <button
            className="edit-button2"
            onClick={() => {
              setEdit(true);
              setProductDetail({
                category: item.category,
                name: item.name,
                source: item.source,
                price: item.price,
                quantity: item.quantity,
                description: item.description,
              });
            }}
          >
            Edit
          </button>
        </div>
      </div>
    </>
  );
}

export default Card;
