import {
  CLOSE_MOUDULE,
  SHOW_DATA,
  SIGNUP_WINDOW,
  SIGNIN_USER_INFO,
} from "../actions/index";
import { combineReducers } from "redux";

export const reducer = (
  state = { isclose: true, signup_isclose: true },
  { type, payload }
) => {
  switch (type) {
    case CLOSE_MOUDULE:
      return { isclose: !state.isclose };
    case SIGNUP_WINDOW:
      return { signup_isclose: !state.signup_isclose };
    default:
      return state;
  }
};

export const SigninReducer = (state = {}, { type, payload }) => {
  switch (type) {
    case CLOSE_MOUDULE:
      return { isclose: !state.isclose };
    default:
      return state;
  }
};
export const showReducer = (state = [], { type, payload }) => {
  switch (type) {
    case SHOW_DATA:
      console.log(payload.result);
      return [...payload];
    default:
      return state;
  }
};

export default combineReducers({
  reducer: reducer,
  show: showReducer,
});
