import React, { useState } from "react";
import Sort from "./Sort";
import Listing from "./Listing";
import "./Product.css";
function Product() {
  const [ifclick, setIfclick] = useState(false);
  // const style = { background: ifclick ? "5048e5" : "white" };
  const background = ifclick ? "5048e5" : "white";

  return (
    <>
      <div className="header-part">
        <h2>Products</h2>
        <div className="header-right">
          <div className="sorting">
            <Sort />
          </div>

          <div className="add">
            <button className="product-add">Add Product</button>
          </div>
        </div>
      </div>
      <div className="body-part">
        <Listing />
      </div>
      <div className="footer-part">
        <button className="left-button">{"<<"}</button>
        <button
          className="middle-button"
          style={{ backgroundColor: background }}
          onClick={() => {
            setIfclick(!ifclick);
          }}
        >
          1
        </button>
        <button className="middle-button">2</button>
        <button className="middle-button">3</button>
        <button className="middle-button">4</button>
        <button className="middle-button">5</button>
        <button className="right-button">{">>"}</button>
      </div>
    </>
  );
}

export default Product;
