import React, { useState } from "react";
import "./Card.css";

function Card({ item }) {
  const [openAdd, setopenAdd] = useState(false);
  return (
    <>
      <div className="box">
        <img src={item.source} alt="logo" className="image" />
        <div className="name">
          <span>{item.name}</span>
        </div>
        <div className="price">
          <span>${item.price}</span>
        </div>
        <div className="buttom-part">
          {openAdd ? (
            <>
              <div className="adding-extend">
                <button className="minus">-</button>
                <div className="number">
                  <span>{item.quality}</span>
                </div>

                <button className="add">+</button>
              </div>
            </>
          ) : (
            <button className="edit-button1" onClick={() => setopenAdd(true)}>
              Add
            </button>
          )}

          <button className="edit-button2">Edit</button>
        </div>
      </div>
    </>
  );
}

export default Card;
