import React, { useState } from "react";
import { addProductdata } from "../actions/index";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import Sort from "./Sort";
import Listing from "./Listing";
import "./Product.css";
import ProductDetail from "./ProductDetail";

function Product() {
  const dispatch = useDispatch();
  const [ifclick, setIfclick] = useState(false);
  const [addProduct, setAddproduct] = useState(false);
  const [imagelink, setimgelinke] = useState("");
  const [imgesource, setimgesource] = useState("");
  const [productName, setproductName] = useState("");
  const [productDescription, setproductDescription] = useState("");
  const [productCategory, setproductCategory] = useState("Category1");
  const [productprice, setproductprice] = useState("");
  const [productQuantity, setproductQuantity] = useState("");
  const [mainpage, setMainpage] = useState(false);
  const [detailpage, setDetailpage] = useState(false);

  const background = ifclick ? "#5048e5" : "white";
  const onchange_text = (e) => {
    setimgelinke(e.target.value);
    console.log(imagelink);
  };
  const content = {
    name: productName,
    description: productDescription,
    category: productCategory,
    price: productprice * 1,
    source: imagelink,
    quantity: productQuantity * 1,
  };
  const adding = () => {
    console.log(content);
    addProductdata(dispatch)(content);
    setproductName("");
    setproductDescription("");
    setproductCategory("");
    setimgesource("");
    setproductprice("");
    setproductQuantity("");
    setMainpage(true);
  };

  return (
    <>
      {!addProduct || mainpage ? (
        <>
          <div className="header-part">
            <h2>Products</h2>
            <div className="header-right">
              <div className="sorting">
                <Sort />
              </div>

              <div className="add">
                <button
                  className="product-add"
                  onClick={() => {
                    setAddproduct(true);
                    setMainpage(false);
                  }}
                >
                  Add Product
                </button>
              </div>
            </div>
          </div>
          <div className="body-part">
            <Listing />
          </div>
          <div className="footer-part">
            <button className="left-button">{"<<"}</button>
            <button
              className="middle-button"
              style={{ backgroundColor: background }}
              onClick={() => {
                setIfclick(!ifclick);
              }}
            >
              1
            </button>
            <button
              className="middle-button"
              style={{ backgroundColor: background }}
              onClick={() => {
                setIfclick(!ifclick);
              }}
            >
              2
            </button>
            <button
              className="middle-button"
              style={{ backgroundColor: background }}
              onClick={() => {
                setIfclick(!ifclick);
              }}
            >
              3
            </button>
            <button
              className="middle-button"
              style={{ backgroundColor: background }}
              onClick={() => {
                setIfclick(!ifclick);
              }}
            >
              4
            </button>
            <button
              className="middle-button"
              style={{ backgroundColor: background }}
              onClick={() => {
                setIfclick(!ifclick);
              }}
            >
              5
            </button>
            <button
              className="right-button"
              onClick={() => {
                setIfclick(!ifclick);
              }}
            >
              {">>"}
            </button>
          </div>
        </>
      ) : (
        <>
          {detailpage ? (
            <>
              <ProductDetail />
            </>
          ) : (
            <>
              <div className="outline">
                <h2>Create product</h2>
                <div className="form">
                  <div className="name-product">
                    <span>Product name</span>
                    <br />
                    <input
                      type="text"
                      value={productName}
                      onChange={(e) => setproductName(e.target.value)}
                    />
                  </div>
                  <div className="description">
                    <span>Product Description </span>
                    <br />
                    <input
                      type="text"
                      value={productDescription}
                      onChange={(e) => setproductDescription(e.target.value)}
                    />
                  </div>
                  <div className="Category-price">
                    <div className="category">
                      <span>Category</span>
                      <br />
                      <select
                        name=""
                        id=""
                        defaultValue={productCategory}
                        onChange={(e) => setproductCategory(e.target.value)}
                      >
                        <option value="Category1">Category1</option>
                        <option value="Category2">Category2</option>
                        <option value="Category3">Category3</option>
                      </select>
                    </div>
                    <div className="price">
                      <span>price</span>
                      <input
                        type="text"
                        value={productprice}
                        onChange={(e) => setproductprice(e.target.value)}
                      />
                    </div>
                  </div>
                  <div className="quatity-link">
                    <div className="quantity">
                      <span>In stock Quantity</span>
                      <br />
                      <input
                        type="text"
                        value={productQuantity}
                        onChange={(e) => setproductQuantity(e.target.value)}
                      />
                    </div>
                    <div className="image-link">
                      <span>Add image Link</span>
                      <div className="link-input">
                        <input type="text" onChange={(e) => onchange_text(e)} />
                        <button onClick={() => setimgesource(imagelink)}>
                          Upload
                        </button>
                      </div>
                    </div>
                  </div>

                  <div className="image-preview">
                    <img src={imgesource} alt="" />
                  </div>
                  <div className="add-button">
                    <button
                      onClick={() => {
                        adding();
                      }}
                    >
                      Add Product
                    </button>
                  </div>
                </div>
              </div>
            </>
          )}
        </>
      )}
    </>
  );
}

export default Product;
