import React from "react";
import CloseIcon from "@mui/icons-material/Close";
import "./Cart.css";
import CardProduct from "./CartProduct";
import { useSelector } from "react-redux";

function Cart({ setopenCart }) {
  const cartinfo = useSelector((state) => state.addCartReducer);
  const isSignedstatus = useSelector((state) => state.checkSignedIn);
  const product_list = isSignedstatus.product;

  let totalNum = 0;
  let totalPrice = 0;
  cartinfo.forEach((e) => {
    if (e.name == "total") {
      totalNum = e.total;
      totalPrice = e.totalPrice;
    }
  });
  return (
    <>
      <div className="cart-body">
        <div className="header">
          <span className="title-part">Cart</span>
          <span className="number-part">({totalNum})</span>
          <CloseIcon
            className="close"
            onClick={() => {
              setopenCart(false);
            }}
          />
        </div>
        <div className="cartinside-body">
          <div className="Product-list">
            <CardProduct />
            <CardProduct />
            <CardProduct />
            <CardProduct />
          </div>
          <div className="cuppon">
            <h5>Apply Discount Code</h5>
            <div className="cuppon-input">
              <input type="text" />
              <button>Apply</button>
            </div>
          </div>
        </div>
        <div className="checkout-cart">
          <div className="before-checkout">
            <div className="checkout-left">
              <span>Subtotal</span>
              <span>Tax</span>
              <span>Discount</span>
              <span>Estimated total</span>
            </div>
            <div className="checkout-right">
              <span>$499</span>
              <span>$499</span>
              <span className="special-discount">-$499</span>
              <span>$499</span>
            </div>
          </div>

          <button>Continue to checkout</button>
        </div>
      </div>
    </>
  );
}

export default Cart;
