import React, { useEffect, useState } from "react";
import "./Card.css";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { addtocart, UpdatePersonCart, checkSignedIn } from "../actions/index";

function Card({
  item,
  setDetailpage,
  setProductDetail,
  setEdit,
  setEditName,
  set,
}) {
  const dispatch = useDispatch();
  let ShowNum = 0;
  let ifopen = false;
  const cartinfo = useSelector((state) => state.addCartReducer);
  const isSignedstatus = useSelector((state) => state.checkSignedIn);
  const product = isSignedstatus.product;
  const isadmin = isSignedstatus.isAdmin;
  // console.log(product);

  let totalNum = 0;
  const [number, setNumber] = useState(0);
  // let totalPrice = 0;
  product.forEach((e) => {
    if (e.name == item.name) {
      totalNum = e.number;
    }
  });

  // const [number, setNumber] = useState(0);

  // console.log(totalNum);

  const user = isSignedstatus.user;
  cartinfo.forEach((e) => {
    if (e.name === item.name) {
      ShowNum = e.number;
      ifopen = e.openAdd;
    }
  });

  const cart = {
    name: item.name,
    number: totalNum,
    price: item.price,
    openAdd: true,
  };

  const add = () => {
    // setNumber(number + 2);
    setNumber(number + 1);

    addtocart(dispatch)({
      name: item.name,
      number: totalNum,
      price: item.price,
      openAdd: true,
      ifadd: true,
    });

    UpdatePersonCart(dispatch)({
      user: user,
      cart: {
        name: item.name,
        number: totalNum + 1,
        price: item.price,
        openAdd: true,
      },
    });
  };

  const minus = () => {
    // setNumber(number - 1);
    addtocart(dispatch)({
      name: item.name,
      number: totalNum - 1,
      price: item.price,
    });
    UpdatePersonCart(dispatch)({
      user: user,
      cart: {
        name: item.name,
        number: totalNum - 1,
        price: item.price,
        openAdd: true,
      },
    });
  };

  const addbutton = () => {
    //addtocart(dispatch)(cart);
    setNumber(number + 1);
    UpdatePersonCart(dispatch)({
      user: user,
      cart: {
        name: item.name,
        number: number,
        price: item.price,
        openAdd: true,
      },
    });
  };

  return (
    <>
      <div className="box">
        <img
          src={item.source}
          alt="logo"
          className="image"
          onClick={() => {
            setDetailpage(true);
            setProductDetail({
              category: item.category,
              name: item.name,
              source: item.source,
              price: item.price,
              quantity: item.quantity,
              description: item.description,
            });
          }}
        />
        <div className="name">
          <span>{item.name}</span>
        </div>
        <div className="price">
          <span>${item.price}</span>
        </div>
        <div className="buttom-part">
          {ifopen || true ? (
            <>
              <div className="adding-extend">
                <button className="minus" onClick={() => minus()}>
                  -
                </button>
                <div className="number">
                  <span>{number}</span>
                </div>

                <button className="add" onClick={() => add()}>
                  +
                </button>
              </div>
            </>
          ) : (
            <button className="edit-button1" onClick={() => addbutton()}>
              Add
            </button>
          )}
          {isadmin ? (
            <button
              className="edit-button2"
              onClick={() => {
                setEdit(true);
                setEditName(item.name);
                set.setEitdescription(item.description);
                set.setEditCategory(item.category);
                set.setEditPrice(item.price);
                set.setEditQuantity(item.quantity);
                set.setEditLink(item.source);
                set.setId(item.id);
                setProductDetail({
                  category: item.category,
                  name: item.name,
                  source: item.source,
                  price: item.price,
                  quantity: item.quantity,
                  description: item.description,
                });
              }}
            >
              Edit
            </button>
          ) : (
            <></>
          )}
        </div>
      </div>
    </>
  );
}

export default Card;
