import {
  CLOSE_MOUDULE,
  SHOW_DATA,
  SIGNUP_WINDOW,
  SIGNIN_USER_INFO,
  ADD_SIGNUP,
  SIGNIN_STATUS,
  SIGNOUT_STATUS,
  FORGET_WINDOW,
  SHOW_PRODUCT,
} from "../actions/index";
import { combineReducers } from "redux";

export const reducer = (
  state = {
    isclose: true,
    signup_isclose: true,
  },
  { type, payload }
) => {
  switch (type) {
    case CLOSE_MOUDULE:
      return { isclose: !state.isclose };
    case SIGNUP_WINDOW:
      return { signup_isclose: !state.signup_isclose };
    default:
      return state;
  }
};

export const showReducer = (state = [], { type, payload }) => {
  switch (type) {
    case SHOW_DATA:
      return [...payload.result];
    case ADD_SIGNUP:
      console.log(payload);
      return [...state, { ...payload }];
    default:
      return state;
  }
};

export const checkSignedIn = (state = false, { type, payload }) => {
  switch (type) {
    case SIGNIN_STATUS:
      console.log("reducer sign in status check:" + payload.status);
      return payload.status;
    case SIGNOUT_STATUS:
      console.log("reducer siout in status check:" + payload.status);
      return payload.status;
    default:
      return state;
  }
};
export const showProduct = (state = [], { type, payload }) => {
  switch (type) {
    case SHOW_PRODUCT:
      return [...payload.result];
    default:
      return state;
  }
};
export default combineReducers({
  reducer: reducer,
  show: showReducer,
  checkSignedIn: checkSignedIn,
  showProduct: showProduct,
});
