import { CLOSE_MOUDULE, SHOW_DATA } from "../actions/index";
import { combineReducers } from "redux";

export const reducer = (state = { isclose: true }, { type, payload }) => {
  switch (type) {
    case CLOSE_MOUDULE:
      return { isclose: !state.isclose };
    default:
      return state;
  }
};

export const showReducer = (state = [], { type, payload }) => {
  switch (type) {
    case SHOW_DATA:
      console.log("hah");
      return [...payload];
    default:
      return state;
  }
};
export default combineReducers({
  reducer: reducer,
  show: showReducer,
});
