import {
  CLOSE_MOUDULE,
  SHOW_DATA,
  SIGNUP_WINDOW,
  SIGNIN_USER_INFO,
  ADD_SIGNUP,
  SIGNIN_STATUS,
  SIGNOUT_STATUS,
  FORGET_WINDOW,
} from "../actions/index";
import { combineReducers } from "redux";

export const reducer = (
  state = {
    isclose: true,
    signup_isclose: true,
    ifSiginedin: false,
    openforgetwindow: false,
  },
  { type, payload }
) => {
  switch (type) {
    case CLOSE_MOUDULE:
      return { isclose: !state.isclose };
    case SIGNUP_WINDOW:
      return { signup_isclose: !state.signup_isclose };
    case SIGNIN_STATUS:
      console.log("inside");
      console.log(payload);
      return { ifSiginedin: payload.status };
    case SIGNOUT_STATUS:
      return { ifSiginedin: false };
    case FORGET_WINDOW:
      console.log("open window");
      console.log(openforgetwindow);
      return { openforgetwindow: true };

    default:
      return state;
  }
};

// export const SigninReducer = (state = {}, { type, payload }) => {
//   switch (type) {
//     case SIGNIN_USER_INFO:
//       return { email: payload.email, password: payload.password };
//     default:
//       return state;
//   }
// };
export const showReducer = (state = [], { type, payload }) => {
  switch (type) {
    case SHOW_DATA:
      // console.log("before");
      // console.log(payload.result);
      return [...payload.result];
    case ADD_SIGNUP:
      console.log(payload);
      return [...state, { ...payload }];
    default:
      return state;
  }
};

export default combineReducers({
  reducer: reducer,
  show: showReducer,
});
