import React, { useEffect, useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import PersonIcon from "@mui/icons-material/Person";
import CircleIcon from "@mui/icons-material/Circle";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import "./Header.css";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { alldata } from "../redux/closeSlice";
import {
  closeModal,
  initdata,
  signupModal,
  signoutStatus,
  PersonalCart,
  ListingCart,
  checkSignedIn,
  checksigninStatus,
  updateCart,
  userlogout,
  tokenpart,
} from "../actions/index";

function Header({ setopenCart }) {
  const dispatch = useDispatch();
  const isSignedstatus = useSelector((state) => state.checkSignedIn);
  //const cartinfo = useSelector((state) => state.addCartReducer);
  const Cartpersonal = useSelector((state) => state.addCartReducer);
  const Tokencuurent = useSelector((state) => state.showCartProduct);
  const Cartpersonal12 = useSelector((state) => state.UpdateCartinfo);
  const islogout = useSelector((state) => state.islogout);
  console.log(Tokencuurent.email);

  const token = Tokencuurent.Cart;
  const name = isSignedstatus.user;

  let product = isSignedstatus.product;

  const newadd = Cartpersonal[0].total;

  let shownumber;
  // console.log(token);
  let totalNum = 0;
  let totalPrice = 0;
  if (token) {
    shownumber = token[0].number;
    token.forEach((e) => {
      totalNum = totalNum + e.number;
      totalPrice = totalPrice + e.price * e.number;
    });
  }
  useEffect(() => {
    ListingCart(dispatch)();
  }, []);

  // product.forEach((e) => {
  //   totalNum = totalNum + e.number;
  //   totalPrice = totalPrice + e.price * e.number;
  // });

  // });
  // useEffect(() => {
  //   tokenpart(dispatch)(1);
  // });

  // let totalNum = 0;
  // let totalPrice = 0;

  // product.forEach((e) => {
  //   totalNum = totalNum + e.number;
  //   totalPrice = totalPrice + e.price * e.number;
  // });

  return (
    <>
      <div className="headerContainer">
        <div className="logo">
          <div className="logoPart">
            <p>Management</p>
          </div>
          <div className="small">
            <p>Chuwa</p>
          </div>
        </div>

        <div className="searchPart">
          <input placeholder="Search" type="text"></input>

          <SearchIcon className="search_icon" />
        </div>

        <div className="right-header">
          <div className="signIn">
            <PersonIcon fontSize="large" className="signin_icon" />

            {/* {!isSignedstatus.statelogin ? ( */}
            {/* {!Tokencuurent ? ( */}
            {true ? (
              <p onClick={() => closeModal(dispatch)()}>Sign In</p>
            ) : (
              <>
                {/* <p onClick={() => signoutStatus(dispatch)()}>Sign Out</p> */}
                <p onClick={() => userlogout(dispatch)()}>Sign Out</p>
              </>
            )}
          </div>
          <div
            className="cart"
            onClick={() => {
              setopenCart(true);
            }}
          >
            <ShoppingCartIcon
              fontSize="medium"
              style={{ color: "white" }}
              className="cart_icon"
            />
            <CircleIcon
              fontSize="small"
              style={{ color: "red" }}
              className="message_icon"
            />
            {/* <span className="message-number">{totalNum + newadd}</span> */}
            <span className="message-number">{totalNum}</span>

            <p>${totalPrice}.00</p>
          </div>
        </div>
      </div>
    </>
  );
}

export default Header;
