export const CLOSE_MOUDULE = "closemodule";
export const SHOW_DATA = "init";
export const closeModal = (dispatch) => () => {
  dispatch({
    type: CLOSE_MOUDULE,
    payload: {
      isclose: true,
    },
  });
};

