export const initdata = (dispatch) => async () => {
  try {
    const response = await fetch("/alldata");

    const result = await response.json();

    console.log("okok" + result);
    dispatch({
      type: SHOW_DATA,
      payload: {
        result,
      },
    });
  } catch (error) {
    console.log(error);
  }
};
